package com.sap.tc.moin.repository.jmigenerator.template;

import java.util.List;

/**
 * The common super interface for all java generation templates.
 * <p>
 * Has anything needed for generating any Java file
 */
public interface JmiGeneratorBaseTemplate {

    /**
     * The leading comment ("Generated by...")
     * 
     * @return the leading comment
     */
    public String getPreamble( );

    /**
     * @return the package name for the class/interface in java format
     */
    public String getPackageName( );

    /**
     * @return the Java class/interface name
     */
    public String getJavaName( );

    /**
     * @return the implemented interfaces (fully qualified), or an empty List
     */
    public List<String> getImplements( );

    /**
     * @return the extended base classes/interfaces (fully qualified) or an
     * empty List
     */
    public List<String> getExtends( );

    /**
     * @return does this class/interface belong to the MOF metamodel
     */
    public boolean getBelongsToMof( );
}
