package com.sap.tc.moin.repository.spi.s2x.serializer;

import com.sap.tc.moin.repository.spi.ps.serializer.SpiElement;
import com.sap.tc.moin.repository.spi.ps.serializer.SpiStructuredValue;

/**
 * This interface can be implemented by MOIN customers in order to have control
 * over the content of the S2X translation content which is generated by MOIN.
 * <p>
 * The direct implementation of this interface is discouraged, because in this
 * case the custom code has to be adapted when the interface is changed or
 * enhanced. Instead, the abstract class {@link SpiS2XSerializerDefaultCallback}
 * should be subclassed.
 */
public interface SpiS2XSerializerCustomCallback {

    /**
     * Returns the translation unit that is going to be serialized into the S2X
     * translation content.
     * <p>
     * Original text, translation unit ID, resource type and maximum width are
     * always set by MOIN and can not be set via this method.
     * 
     * @param translatableTextAttributeParent The class with the translatable
     * text attribute
     * @param translatableTextAttributeName The translatable text attribute
     * @param translatableTextValue The value of the translatable text attribute
     */

    public SpiS2XTranslationUnit getTranslationUnit( SpiElement translatableTextAttributeParent, String translatableTextAttributeName, SpiStructuredValue translatableTextValue );
}
