package com.sap.tc.moin.repository.spi.s2x.serializer;

/**
 * This abstract class should be subclassed by MOIN customers in order to have
 * control over the content of the S2X translation content which is generated by
 * MOIN.
 * <p>
 * It implements the interface {@link SpiS2XSerializerCustomCallback}.
 */
public abstract class SpiS2XSerializerDefaultCallback implements SpiS2XSerializerCustomCallback {

    public SpiS2XSerializerDefaultCallback( ) {
        // default constructor needed for reflection API
    }
}
