package com.sap.tc.moin.xm.generation.core.code.util;

import java.util.List;


/**
 * Common parts of the XM Parser/Serailizer template.
 */
public interface XmParserSerializerTemplate {

    /** Returns the name of the parser/serializer class. */
    String getJavaName( );

    /**
     * Returns a list of full qualified class names which extend the
     * parser/serializer class.
     */
    List<String> getExtends( );

    /**
     * The leading comment ("Generated by...")
     * 
     * @return the leading comment
     */
    String getPreamble( );

    /**
     * @return the package name for the class/interface in java format
     */
    String getPackageName( );


    /**
     * @return the implemented interfaces (fully qualified), or an empty List
     */
    List<String> getImplements( );


    /** Returns the name of the schema for which this parser has been generated. */
    String getSchemaName( );

    /** Returns a list containing the prefixes and the corresponding namespaces. */
    List<PrefixAndNamespace> getPrefixes( );

}
